// @ts-check
import getBreakpoints from "0.8.1/astro-imagetools/api/utils/getBreakpoints.js";

export default function getConfigOptions(
  imageWidth,
  imagesizes,
  breakpoints,
  format,
  imageFormat,
  fallbackFormat,
  includeSourceFormat
) {
  const formats = [
    ...new Set(
      [format, includeSourceFormat && imageFormat]
        .flat()
        .filter((f) => f && f !== fallbackFormat)
    ),
    fallbackFormat,
  ];

  const requiredBreakpoints = getBreakpoints(breakpoints, imageWidth);

  imagesizes =
    typeof imagesizes === "string"
      ? imagesizes
      : imagesizes(requiredBreakpoints);

  return {
    formats,
    imagesizes,
    requiredBreakpoints,
  };
}
